  }
  .first-open__description p {
    font-size: 24px;
    margin-bottom: 30px;
  }

  .first-open__picture {
    margin-top: 50px;
  }
  .first-open__picture img {
    width: 80vw;
  }

  .shop-all {
    background-position: -400px;
  }

  .shop-all__wrap {
    justify-content: end;
  }
  .shop-all__wrap a {
    margin-bottom: 40px;
  }

  .review:nth-of-type(2n+1) {
    position: absolute;
    z-index: -1;
  }

  .recent-items__card p:first-of-type {
    font-size: 24px;
  }

  .card__picture {
    height: 380px;
  }

  .add-to-cart {
    bottom: 32vw;
    background-color: #FF5050;
    width: 70%;
    color: #fff;
  }

  .fandoms__items {
    flex-flow: column-reverse wrap;
    max-height: 430px;
    width: 300px;
    margin: 0 auto 20px;
  }
  .fandoms__items img {
    width: 100px;
    margin: 8px 0;
  }

  .footer__wrap {
    align-items: start;
  }

  .footer__logo {
    margin-bottom: 25px;
  }

  .footer__sitemap, .footer__contact {
    width: 90%;
    justify-content: space-between;
    margin: 30px 0;
  }

  .sitemap__block-1,
.sitemap__block-2 {
    gap: 3vw;
  }
}
/*Mobile phones*/
@media screen and (max-width: 520px) {
  .navbar {
    display: block;
  }

  h1 {
    font-size: 76px;
  }

  .large {
    font-size: 25px;
  }

  .card__picture {
    height: 70%;
  }

  .nav-left,
.nav-right a:nth-of-type(1) {
    display: none;
  }

  .shop-all {
    background-position: -600px;
  }

  .geek-on__blocks {
    align-items: center;
    flex-direction: column;
  }
  .geek-on__blocks img {
    width: 80vw;
  }

  .header__logo {
    display: block;
    transform: scale(0.5) translateX(-80px);
  }

  .header__nav {
    justify-content: flex-end;
  }

  .footer__legal-info {
    padding: 15px;
  }
  .footer__legal-info p {
    font-size: 12px;
  }

  .fandoms__items img {
    width: 100px;
  }

  .burger {
    display: block;
  }
}

sources.style.scss:
/* ......../ variables /.......... */
$accent: #FF5050;
$white: #ffffff;
$black: #0d0d0d;
$background: #f0f0f0;

/* ......../ style reset /.......... */
* {
  margin: 0;
  padding: 0;
  text-decoration: none;
  border: none;
  outline: none;
}

html {
  font-family: "Roboto", sans-serif;
  box-sizing: border-box;
  scroll-behavior: smooth;
}

main {margin: 0 auto;}

*,
*::before,
*::after {
  box-sizing: inherit;
}

:focus {
  outline: none;
}

ol,
ul {
  list-style: none;
}

input,
button,
textarea,
select {
  font: inherit;
}

button {
  cursor: pointer;
}

a {
  outline: none;
  text-decoration: none;
  color: $black;
  text-transform: uppercase;
}

/* ......../ common style set /.......... */
h1, h2, h3, h4, h5, a, button, span {
  font-family: "Roboto Condensed", sans-serif;
  font-weight: bold;
  text-transform: uppercase;
}

.center {text-align: center;}

span {font-size: 42px;}
h1 {font-size: 95px;}

h2 {
  font-size: 50px;
  margin-bottom: 1.56vw;
}

h3 {font-size: 26px;}
h4 {font-size: 22px;}
h5, a {font-size: 20px;}

.navbar {display: none;}

.nav-link {
  font-size: 14px;
  font-weight: medium;
}

p {font-size: 18px;}
.small {font-size: 16px;}
.large {font-size: 30px;}

li + li {margin-top: 20px;}

.btn {
  width: 150px;
  height: 60px;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: $accent;
  color: $white;
}

.btn-inv {
  background-color: $white;
  color: $black;
}

.gift::before, .li-gift li:before {
  content: "üéÅ";
  padding-right: 2px;
}

.nav-link {font-weight: medium;}
.footer__subscribe-form {position: relative;}

[type="email"] {
  color: $white;
  width: 100%;
  padding-bottom: 15px;
  background-color: #262425;
  border-bottom: 1px solid $white;
}

[type="submit"] {
  position: absolute;
  background-color: #262425;
  right: 0px;
  &::after {
    content: url("assets/svg/arrow.svg");
    &:hover {
      content: url("assets/svg/arrow-hover.svg");
    }
  }
}


.burger {
  position: absolute;
  top: 38px;
  left: 0;
  display: block;
  z-index: 1;
}

#burger {
  display: block;
  margin-bottom: 5px;
  margin-left: 30px;
  width: 40px;
  height: 32px;
  position: absolute;

  &:checked ~ ul {
    transform: none;
  }

  cursor: pointer;

  opacity: 0;
  z-index: 2;

  &:checked ~ span {
    opacity: 1;
    background-color: $accent;
  }
}
#menu {
  position: absolute;
  width: 100vw;
  height: 100vh;
  padding: 50px;
  padding-top: 45px;
  
  background-color: $white;
  transform: translate(-150%, 0);
  transition: transform ease 0.5s;
}
#menu li
{
  border-bottom: 1px solid rgba(0, 0, 0, 0.086);
  padding: 20px 0;
  font-size: 18px;
}

.burger span {
  display: block;
  width: 33px;
  height: 4px;
  margin-bottom: 5px;
  margin-left: 30px;
  position: relative;
  
  background: #0d0d0d;
  border-radius: 3px;
  
  z-index: 1;
  transform-origin: 4px 0px;
}

.burger {
  display: none;
}

.btn:hover {
  border: 1px solid $accent;
  color: $accent;
  background-color: #fff;
  box-shadow: 10px 10px 0 $accent;
  transition: all ease 0.5s; 
}

.opacity:hover {
  color: $white;
  border: 1px solid $white;
  background-color: transparent;
}

.btn-inv:hover {
  box-shadow: 10px 10px 0 #fff;
  transition: all ease 0.4s;
  border: 1px solid $white;
  background-color: $accent;
  color: $white;
}
img:hover {
  transform: scale(1.1);
  transition: transform ease 0.4s;
}

li:hover {
  opacity: 1;
  transform: scale(1.02);
}

[class*="wrap"]:not([class*="header"]) {
  margin: 0 auto;
  padding-top: 100px;
  padding-bottom: 100px;
  max-width: 1220px; 
}

footer button:hover {
  border: none;
  background-color: transparent;
  transform: scale(1.2);
  &::after {
    content: url("assets/svg/arrow-hover.svg");
  }
}

[type="email"]:focus {
  border-bottom: 3px solid $accent;
}

.header__wrap,
.footer__wrap,
.footer__legal-info {
  max-width: 1920px;
  margin: 0 auto;
}

/* ......../ header /.......... */
header {
  background-color: $white;
}

.header__wrap {
  padding: 0 25px;
  position: relative;
  height: 100px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.header__nav {
  padding-right: 15px;
  position: relative;
  max-width: 1920px;
  width: 90%;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.header__logo {
  position: absolute;
  margin: 0 auto;
}

.nav-right a:first-of-type {
  position: relative;
  bottom: 4px;
}

.nav-left a:hover {
  padding-bottom: 5px;
  border-bottom: 4px solid $accent;
}

.nav-right a:hover,
.footer__sitemap a:hover,
.burger a:hover {
  color: $accent;
}

.header__nav a + a {margin-left: 3vw;}

/* ......../ first-screen /.......... */
.promo {
  width: 100%;
  height: 600px;
  background: url("assets/img/bg-1.png"), $accent;
  background-repeat: no-repeat;
  background-size: cover;
  color: $white;
}

.promo__wrap {height: 100%;}

.promo__description {
  max-width: 400px;
  height: 100%;
  animation: entrance 1s;

  h1 {
    line-height: 90px;
    margin-bottom: 30px;
  }
  p {margin-bottom: 2.5vw;}
}

/* ......../ offers screen /.......... */

.offer__wrap {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10vw;
  margin: 5vw auto;

  h2 {
    width: 25vw;
    span {
      font-size: inherit;
      color: $accent;
    }
  }
  li {
    max-width: 555px;
    color: $black;
    opacity: 0.7;
    &::before {
      position: relative;
      top: 5px;
      margin-right: 10px;
      content: url("assets/svg/tick.svg");
    }
  }
}

/* ......../ box paragraoh /.......... */
.box__wrap {
  height: 545px;
  padding: 85px 75px;
  display: flex;
  justify-content: space-around;
  align-items: center;
  background-color: #FFF6F6;
  
  @for $i from 1 through 4 {
    li:nth-of-type(#{$i})::before {
      margin-right: 5px;
      content: url("assets/svg/dec-#{$i}.svg");
    }
  }
}

@for $i from 1 through 6 {
  .box__pictures img:nth-of-type(#{$i}) {
    grid-area: p#{$i};
  }
}

.box {margin-bottom: 6vw;}
.box__description {max-width: 400px; opacity: 0.7;}

.box__pictures {
  display: grid;
  grid-template-areas: 
  "p1 p2 p3"
  "p1 p2 p3"
  "p1 p5 p3"
  "p4 p5 p6"
  "p4 p5 p6";
  grid-gap: 1.05vw;
}

/* ......../ geek-on screen /.......... */
.geek-on {
  h2 {margin-bottom: 3.125vw;}
  background-color: #F8F8F8;
}

.geek-on__blocks {
  display: flex;
  justify-content: center;
  gap: 2.865vw;
}

.geek-on__block {
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 390px;
  h3 {margin: 1.3vw 0;}
  p {
    color: $black;
    margin-top: 2.08vw;
    opacity: 0.7;
    line-height: 150%;
  }
  img {
    max-width: 100%;
    width: 30vw;
  }
}

.block__card {background-color: $white;}

/* ......../ gallery section /.......... */
.gallery__wrap {
  h5 {color: $accent; margin-bottom: 30px;}
  position: relative;
  & > img {
    max-width: 250px;
    height: 250px;
  }
}

.gallery__headline {grid-area: e;}
.gallery__section .btn {grid-area: b;}

.gallery__section {
  img {
    width: 100%;
  }
  position: relative;
  display: grid;
  grid-template-columns: 1fr 1.5fr 1fr 1fr 1.5fr 1fr;
  grid-template-rows: auto;
  justify-content: end;
  align-items: center;
  gap: 1.25vw;

  @for $i from 1 through 9 {
    img:nth-of-type(#{$i}) {
      grid-area: e#{$i};
    }
  }
  grid-template-areas: 
  "e e e . . e8"
  "e e e e5 e7 e8"
  ". e2 e3 e5 e7 e9"
  "e1 e2 e3 e6 e7 e9"
  "e1 e2 e4 e6 e7 ."
  ". e2 e4 . . b";

  .btn {
    max-width: 100%;
    position: absolute;
    bottom: 0;
  }
}

/* ......../ advantages paragraph /.......... */
.advantages {margin-bottom: 15vw;}

.advantages__wrap {
  max-width: 1220px;
  padding: 80px 65px;
  display: flex;
  background-color: #F8F8F8;
  p {
    max-width: 250px;
    margin: 1.56vw 1.56vw 0 0;
  }
}

/* ......../ price section /.......... */
.plans {background-color: #F8F8F8;}

.plans__wrap {
  padding: 5vw 0;
  & > p:last-of-type {
    max-width: 570px;
    margin: 0 auto;
  }
  h2 + p {margin-bottom: 4.16vw;}
} 

.plans__cards, .recent-items__cards {
  display: flex;
  justify-content: space-between;
  background-color: #F8F8F8;
  margin-bottom: 5.2vw;
}

.plans__card {
  display: flex;
  flex-direction: column;
  justify-content: center;
  position: relative;
  background-color: #fff;
  width: 31%;
  padding: 2vw 0 0 1.5vw;
  transition: all ease 0.2s;

  p:first-of-type {
    font-size: 46px;
    font-weight: 600;
    margin-bottom: 1.56;

    &::before {
      content: "$";
      font-size: 34px;
      font-weight: bold;
    }

    &::after {
      content: "/ month";
      font-size: 22px;
      font-weight: normal;
      line-height: 24px;
      color: $black;
      opacity: 0.5;
    }
  }

  &:hover {
    transform: scale(1.05);
    box-shadow: 0px 0px 25px 0px #23242725;
  }

  p {
    font-size: 20px;
    margin: 30px 0 16px;
  }

  ul {
    margin-bottom: 90px;
    line-height: 0.9vw;

    li::before {
      content: url("assets/img/chk.png");
      padding-right: 10px;
    }
  }

  .btn {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
  }

  li.disabled {
    opacity: 0.3;
    &::before {
      content: url("assets/img/none.png");
    }
  }
}

/* ......../ first-open section /.......... */
.first-open {padding: 5vw 0;}

.first-open__wrap {
  display: flex;
  justify-content: space-between;
}

.first-open__description {
  max-width: 400px;
  margin-right: 2.45vw;
  p {
    margin-bottom: 2.1vw;
    line-height: 1.4;
  }
}

.first-open__picture {
  max-width: 100%;
  display: flex;
  align-items: center;
  background-color: #F1F4F9;
}

.shop-all {
  background: url("assets/img/bg-2.png");
  background-repeat: no-repeat;
  background-size: cover;
}

.shop-all__wrap {
  color: $white;
  height: 940px;
  display: flex;
  flex-direction: column;
  align-items: start;
  justify-content: center;

  h5 {margin-bottom: 1.05vw;}
  p {
    max-width: 385px;
    margin-bottom: 2.08vw;
  }
}


/* ......../ recent-items section /.......... */
.recent-items__wrap {
  padding: 5vw 0;
  h2 {margin-bottom: 4vw;}
}

.recent-items__cards {
  background-color: #fff;
  margin: 0;
}

.recent-items__card {
  width: 31%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  h3 {margin: 1.56vw 0 0.78vw;}
  p {margin-bottom: 15px;}

  .recent-items__card-colors {
    display: flex;
    justify-content: center;
  }

  img {
    width: 40vw;
    max-width: 100%;
  }

  .color {
    width: 16px;
    height: 16px;
    border-radius: 50%;

    &:nth-of-type(1) {background-color: $black;}
    &:nth-of-type(2) {background-color: #BDBDBD;}
    &:nth-of-type(3) {background-color: #EB5757;}
    &:nth-of-type(4) {background-color: #F2C94C;}

    & + .color {margin-left: 10px;}
  }
}

.add-to-cart {
  position: relative;
  bottom: -85px;
  height: 60px;
  width: 98%;
  margin: 0 auto;
  background-color: #fff;
  font-size: 18px;
}

.card__picture {
  height: 387px;
  overflow: hidden;
  &:hover {
    transform: scale(1.05);
    img {
      filter:brightness(0.9);
    }
    .add-to-cart {
      bottom: 80px;
    }
  }
  transition: all ease 0.6s;
}

/* ......../ reviews section /.......... */
.reviews {background-color: #F8F8F8;}

.reviews__wrap {
  padding: 5vw 0;
  & > h5 {
    color: $accent;
    margin-bottom: 50px;
  }
}

.reviews__block {
  display: flex;
  gap: 1.56vw;
  .review {
    background-color: #F8F8F8;
    h5 {
      opacity: 0.4;
      margin-top: 30px;
    }
    &:nth-of-type(odd) {
      opacity: 0.4;
    }
  }
}

/* ......../ fandom section /.......... */
.fandoms__wrap {
  h5 {margin-bottom: 52px;}
  padding: 100px 0;
}

.fandoms__items {
  overflow: hidden;
  display: flex;
  justify-content: center;
  gap: 3.6vw;
  img:hover {
    transform: scale(1.05);
    transition: transform ease 0.4s;
  }
}

/* ......../ footer /.......... */
footer {
  background: #262425;
  color: $white;
}

.footer__wrap {
  a {
    color: $white;
    font-weight: 400;
  }
  padding: 5vw 20px;
  max-width: 1120px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 0.78vw;
  padding: 5vw 0;
}

.footer__legal-info {
  padding: 22px 0;
  opacity: 0.2;
  display: flex;
  justify-content: space-around;
  border-top: 1px solid #bdbdbd6d;
}

.footer__sitemap {
  display: flex;
  justify-content: space-around;
  width: 320px;
}

.sitemap__block-2, .sitemap__block-1 {
  display: flex;
  flex-direction: column;
  gap: 1.05vw;
}

.footer__contact {
  width: 30%;
  height: 125px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.footer__sm {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
}

/* ......../ animation /.......... */
@keyframes entrance {
  0% {
    opacity: 0;
    transform: translateX(-300px);
  }

  100% {
    opacity: 1;
    transform: translateX(0);
  }
}

/* ......../ media query /.......... */
@media screen and (max-width: 1279px) {
  .header__logo {transform: scale(0.7);}
  .gift {display: none;}
  [class*=wrap]:not([class*="header"]) {
    padding: 60px 40px;
    overflow: hidden;
  }
  .offer__wrap h2 {
    width: 40%;
  }
  .promo {background-position: -400px;}
  h2 {font-size: 40px;}
  .card__picture {height: 330px;}
  .plans img {transform: scale(0.75);}
  .small {font-size: 14px;}
  .first-open__wrap {
    flex-direction: column;
    align-items: center;
  }
  .first-open__description {
    max-width: 90%;
    margin-bottom: 20px;
  }
}

/*Small (Tablets)*/
@media screen and (max-width: 768px) {
  h2 {text-align: center;}
  li {font-size: 20px;}
  .small {font-size: 24px;}
  
  [class*="wrap"]:not(.header__wrap) {
    flex-direction: column;
    padding: 5vw 8vw;
  }
  .header__logo {display: none;}
  .promo {background-position: -900px;}

  .offer__wrap h2 {
    width: 100%;
    text-align: center;
  }
  .offer, .box__wrap {height: 100%;}
  .offer__wrap, .box__wrap {gap: 5vw;}
  .box__description {max-width: 100%;}
  .box__pictures {
    width: 100%;
    img {width: 100%;}
  }
  
  .gallery .gallery__wrap {
    text-align-last: left;
    flex-direction: row;
    & > img {display: none;}
  }

  .gallery__section {
    .btn,
    img:nth-of-type(8) {display: none;}
    
    align-items: end;
    grid-template-columns: 1fr 1.5fr 1fr;
    grid-template-rows: auto;
    grid-template-areas: 
      "e e ."
      "e1 e2 e4"
      "e5 e2 e9"
      "e5 e7 e9"
      "e6 e7 e3";
  }

  .advantage {margin: 1.3vw 6.5vw;}
  .advantages__wrap {
    width: 80%;
    p {
      font-size: 24px;
      max-width: 100%;
      margin-bottom: 30px;
    }
  }

  .plans__cards,
  .recent-items__cards,
  .reviews__block {
    flex-direction: column;
  }

  .plans__card, .recent-items__card {
    padding: 7.8vwx 0 0 7.8vw;
    margin: 40px 0;
    width: 100%;
    img {width: 100%;}
    p:first-of-type {
      font-size: 64px;
      &::before {font-size: 48px;}
    }

    img + p {width: 100%; font-size: 4.7vw; margin: 0 0 15px;}
    ul {line-height: 1.5;}
  }

  .first-open {text-align-last: left;}
  .first-open__description {
    max-width: 100%;
    p {
      font-size: 24px;
      margin-bottom: 30px;
    }
  }
  .first-open__picture {
    margin-top: 50px;
    img {width: 80vw;}
  }

  .shop-all {background-position: -400px;}
  .shop-all__wrap {
    justify-content: end;
    a {margin-bottom: 40px;}
  }

  .review:nth-of-type(2n+1){
    position: absolute;
    z-index: -1;
  }

  .recent-items__card p:first-of-type {
    font-size: 24px;
  }
  .card__picture {height: 380px;}
  .add-to-cart {
    bottom: 32vw;
    background-color: $accent;
    width: 70%;
    color: #fff;
  }

  .fandoms__items {
    img {
      width: 100px;
      margin: 8px 0;
    }
    flex-flow: column-reverse wrap;
    max-height: 430px;
    width: 300px;
    margin: 0 auto 20px;
  }

  .footer__wrap {align-items: start;}
  .footer__logo {margin-bottom: 25px;}
  .footer__sitemap, .footer__contact {
    width: 90%;
    justify-content: space-between;
    margin: 30px 0;
  }
  .sitemap__block-1,
  .sitemap__block-2 {
    gap: 3vw;
  }
}

/*Mobile phones*/
@media screen and (max-width: 520px){
  .navbar {display: block;}

  h1 {font-size: 76px}
  .large {font-size: 25px;}

  .card__picture {height: 70%}

  .nav-left,
  .nav-right a:nth-of-type(1) {
    display: none;
  }

  .shop-all {background-position: -600px;}

  .geek-on__blocks {
    align-items: center;
    flex-direction: column;
    img {width: 80vw;}
  }
  .header__logo {display: block; transform: scale(0.5) translateX(-80px);}
  .header__nav {justify-content: flex-end;}

  .footer__legal-info {
    padding: 15px;
    p {font-size: 12px;}
  }

  .fandoms__items img {
    width: 100px;
  }

  .burger {
    display: block;
  }
}
